import React, { useState } from "react";

import { connect } from "react-redux";
import { selectBuyState } from "../../redux/cart/cart.selectors";
import { toggleBuyState } from "../../redux/cart/cart.action";

import { Link, useHistory } from "react-router-dom";
import { createStructuredSelector } from "reselect";
import { auth } from "../../firebase/firebase";

import {
  SignInContainer,
  SignInSubcontainer,
  Logo,
  Title,
  SignInInput,
  TagName,
  SignInButton,
  RegisterButton,
  Description,
  NewUser,
  Text,
} from "./signin.styles";

const SignIn = ({ buyNow }) => {
  const history = useHistory();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const signIn = (e) => {
    e.preventDefault();

    auth
      .signInWithEmailAndPassword(email, password)
      .then((auth) => {
        console.log("BUY BOW STATE", buyNow);
        if (buyNow) {
          history.push("/address");
        } else {
          history.push("/");
        }
      })
      .catch((error) => alert(error.message));

    //firebase happens here
  };
  return (
    <SignInContainer className="signin">
      <Link to="/">
        <Logo
          className="signin__logo"
          alt=""
          src="https://upload.wikimedia.org/wikipedia/commons/thumb/a/a9/Amazon_logo.svg/1024px-Amazon_logo.svg.png"
        />
      </Link>
      <SignInSubcontainer className="signin__container">
        <Title>Sign-In</Title>
        <form>
          <TagName>E-mail</TagName>
          <SignInInput
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TagName>Password</TagName>
          <SignInInput
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <SignInButton
            type="submit"
            className="signin__signInButton"
            onClick={signIn}
          >
            Sign In
          </SignInButton>
        </form>
        <Description>
          By continuing, you agree to Amazon's Conditions of Use and Privacy
          Notice.
        </Description>
      </SignInSubcontainer>
      <NewUser className="new__user">
        <Text>New to Amazon?</Text>
      </NewUser>
      <Link to="/signup">
        <RegisterButton className="signin__registerButton">
          Create your Amazon account
        </RegisterButton>
      </Link>
    </SignInContainer>
  );
};

const mapStateToProps = () =>
  createStructuredSelector({
    buyNow: selectBuyState,
  });

const mapDispatchToProps = (dispatch) => ({
  toggleBuyState: () => dispatch(toggleBuyState()),
});

export default connect(mapStateToProps, mapDispatchToProps)(SignIn);
