import React from "react";
import SideNavRow from "../sidenav-row/siderow.component";
import SubContainer from "../sidenav-subcontainer/sidenav-subcontainer.component";
import { Transition } from "react-transition-group";
import { useAmazonContext } from "../../context/amazon-context";

const Sidenav = (props) => {
  const { subContainer, setSubContainer } = useAmazonContext();
  return (
    <div
      className="sidenav"
      style={
        props.state === "entering"
          ? { animation: "moveSideBar .3s forwards" }
          : props.state === "entered"
          ? { transform: "translateX(-0px)" }
          : { animation: "moveSideBar .3s reverse backwards" }
      }
    >
      <div className="sidenavHeader">
        <i className="fas fa-user-circle"></i> Hello, Sign In
      </div>
      <Transition in={!subContainer} timeout={300} unmountOnExit mountOnEnter>
        {(state) => <SideNavContent state={state} />}
      </Transition>
      <Transition in={subContainer} timeout={300} unmountOnExit mountOnEnter>
        {(state) => (
          <SubContainer state={state} setSubContainer={setSubContainer} />
        )}
      </Transition>
    </div>
  );
};

export default Sidenav;
