import styled from "styled-components";

export const ComponentContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 15px 25px 15px 25px;
  cursor: pointer;
  :hover {
    background-color: #eaeded;
  }
`;

export const Arrow = styled.span`
  color: black;
`;
