import React from "react";
import { withRouter } from "react-router-dom";

import { connect } from "react-redux";

import Subtotal from "../../components/subtotal/subtotal.component";
import CheckoutItem from "../../components/checkout-item/checkout.component";
import alexwong_banner from "../../assets/alexwong_banner.png";

import {
  CheckOutContainer,
  CheckOutAd,
  CheckOutTitle,
} from "./checkout.styles";

import "./checkout.styles.css";

function Checkout({ cartItems, history, match }) {
  return (
    <CheckOutContainer className="checkout">
      <div className="checkout__left">
        <a href="https://alwportfolio.netlify.app/">
          <CheckOutAd
            className="checkout__ad"
            //src="https://images-na.ssl-images-amazon.com/images/G/02/UK_CCMP/TM/OCC_Amazon1._CB423492668_.jpg"
            src={alexwong_banner}
            alt=""
          />
        </a>

        <div>
          <div className="checkoutItem__container">
            <CheckOutTitle className="checkout__title">
              <h2></h2>Shopping Cart
            </CheckOutTitle>
            {cartItems.map((item) => {
              return (
                <CheckoutItem cartItem={item} history={history} match={match} />
              );
            })}
          </div>
        </div>
      </div>

      <div className="checkout__right">
        <Subtotal />
      </div>
    </CheckOutContainer>
  );
}

const mapStateToProps = (state) => ({
  cartItems: state.cart.cartItems,
});

export default withRouter(connect(mapStateToProps)(Checkout));
