import styled from "styled-components";

const GetElementHeight = (props) => {
  console.log("AAAAAAAAAAAAaaa", props.height);
  if (props.height) {
    return props.height;
  }
};

export const DirectoryMenuContainer = styled.div`
  width: 100%;
  height: ${GetElementHeight}px;
  display: flex;
  flex-direction: column;

  z-index: 10;
  border: green solid 5px;
`;

export const DirectoryMenuSubContainer = styled.div`
  width: 100%;

  display: flex;
  flex-direction: column;
  z-index: 10;
  border: red solid 5px;

  position: absolute;
  
  transform: translateY(-10%);
`;

export const DirectoryMenuFirstRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin: 10px 10px;
`;

export const SpecialCard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 0 7.5px 15px;
`;

export const SpecialCardTop = styled.div`
  margin-bottom: 10px;
`;

export const SpecialCardButton = styled.div``;

export const DirectoryMenuSecondRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin: 10px 10px;
`;
