import styled from "styled-components";

const GetElementHeight = (props) => {
  console.log("GetElementHeight", props.height - 300);

  return props.height - 280;
};

export const ComponentContainer = styled.div`
  width: 100%;
  height: ${GetElementHeight}px;
  display: flex;
  flex-direction: column;
  z-index: 10;
  background-color: #eaeded;
`;

export const ComponentSubContainer = styled.div`
  width: 95%;
  margin-left: auto;
  margin-right: auto;
  display: flex;
  flex-direction: column;
  z-index: 10;
  position: relative;
  top: -300px;
`;

export const RowsContainer = styled.div`
  border: solid red 1px;
  width: 1400px;
  margin-left: auto;
  margin-right: auto;
`;

export const FirstRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin: 0px 0px 0px 0px;
`;

export const SpecialCard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  //margin: 0 7.5px 15px;
`;

export const SpecialCardTop = styled.div`
  margin-bottom: 10px;
`;

export const Button = styled.div``;

export const SecondRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin: 0px 0px 0px 0px;
`;
