import React, { useState, useRef, useEffect } from "react";
import { IoIosArrowDown, IoIosArrowUp } from "react-icons/io";
import { Transition } from "react-transition-group";

import SideNavRow from "../siderow/sidenav-row.component";

import {
  DropdownContainer,
  SidenavDropdown,
  DropdownArrow,
} from "./dropdown.styles";

const DropDown = ({ entries, ...otherProps }) => {
  console.log("QUE ES ENTIRES", entries);
  const [openDropDown, setOpenDropDown] = useState(false);

  const [entriesLength, setEntriesLength] = useState(0);
  const sideNavContainer = useRef(null);

  let arrowDirection = <IoIosArrowDown />;

  if (!openDropDown) {
    arrowDirection = <IoIosArrowDown />;
  } else {
    arrowDirection = <IoIosArrowUp />;
  }

  useEffect(() => {
    getLength(entries);
  }, []);

  const getLength = (entries) => {
    entries.map((entry) => {
      let a = entry.length;
      return a;
    });
  };

  return (
    <>
      <Transition
        mountOnEnter
        unmountOnExit
        in={openDropDown}
        timeout={{ enter: 300, exit: 200 }}
      >
        {(state) => (
          <DropdownContainer
            {...otherProps}
            entries
            className="dropDownContainer"
            // style={
            //   state === "entering"
            //     ? {
            //         animation: "dropDown .3s forwards",
            //         height: `${51 * entries.length}px`,
            //       }
            //     : state === "entered"
            //     ? {
            //         transform: "scaleY(1)",
            //         opacity: "1",
            //         height: `${51 * entries.length}px`,
            //       }
            //     : {
            //         animation: "dropDown .2s reverse backwards",
            //         transition: "height 0.2s",
            //       }
            // }
            ref={sideNavContainer}
          >
            <hr />
            {entries.map((entry) => (
              <SideNavRow text={entry.title} />
            ))}
          </DropdownContainer>
        )}
      </Transition>
      <SidenavDropdown
        className="sidenavRowDropdown"
        onClick={() => setOpenDropDown((prevState) => !prevState)}
      >
        <div>See All</div>
        <DropdownArrow className="dropdown__arrow">
          {arrowDirection}
        </DropdownArrow>
      </SidenavDropdown>
    </>
  );
};

export default DropDown;
