import React, { useState } from "react";
import { useForm, Controller } from "react-hook-form";
import Select from "react-select";

import HeaderImage from "../../assets/address-page-header.png";

import { Dropdown } from "semantic-ui-react";
import _ from "lodash";
import faker from "faker";

import "./address-page.styles.css";
import { countryOptions } from "./address-page.data";

const addressDefinitions = faker.definitions.address;

const AdressPage = () => {
  const [values, setValues] = useState({
    firstName: "",
    userType: "",
  });

  const {
    register,
    errors,
    control,
    handleSubmit,
    setValue,
    triggerValidation,
  } = useForm();

  const onFormSubmit = (data) => console.log(data);

  const onErrors = (errors) => console.error(errors);

  const onChange = (event, result) => {
    const { name, value } = result || event.target;
    setValues({ ...values, [name]: value });
  };
  console.log(values);
  return (
    <div className="addressPage__container">
      <div className="addressPage__subContainer">
        <div className="addressPage__header">
          <img alt="" src={HeaderImage}></img>
        </div>
        <h1 className="addressPage__title">Select a shipping address</h1>
        <hr />
        ADDRESS CARD
        <hr />
        <form onSubmit={handleSubmit(onFormSubmit, onErrors)}>
          <div className="addressPage__firstRow">
            <h3>Add new Address</h3>
            <span>Country/Region</span>
            {/* <Dropdown
              name="country"
              onChange={onChange}
              value={values.country}
              placeholder=""
              search
              selection
              options={countryOptions}
              className="addressPage__stateInput"
            /> */}

            <Controller
              name="country"
              control={control}
              defaultValue={false}
              rules={{ required: true }}
              render={({ field }) => (
                <Select
                  {...field}
                  className="addressPage__stateInput"
                  classNamePrefix="addressPage__stateInput"
                  options={countryOptions}
                />
              )}
            />

            <span>Full name (First and Last name</span>
            <input
              {...register("name", { required: true })}
              type="text"
              placeholder=""
            ></input>
            <span>Phone number</span>
            <input
              {...register("phone", { required: true })}
              type="text"
              placeholder=""
            ></input>
            <span>Address</span>
            <input
              {...register("nameStreet", { required: true })}
              type="text"
              placeholder="Street address or P.O. Box"
            ></input>
            <input
              name="numberStreet"
              {...register("numberStreet", { required: true })}
              type="text"
              placeholder="Apt, suite, unit, building, floor, etc."
            ></input>
          </div>
          <div className="addressPage__secondRow">
            <div className="addressPage__inputContainer">
              <span>City</span>
              <input
                {...register("city", { required: true })}
                className="addressPage__cityInput"
                type="text"
                placeholder=""
              ></input>
            </div>

            <div className="addressPage__inputContainer">
              <span>State</span>
              {/* <Dropdown
                name="state"
                placeholder="State"
                search
                selection
                options={stateOptions}
                className={"addressPage__stateInput"}
              /> */}

              <Controller
                name="country"
                control={control}
                defaultValue={false}
                rules={{ required: true }}
                render={({ field }) => (
                  <Select
                    {...field}
                    className="addressPage__stateInput"
                    classNamePrefix="addressPage__stateInput"
                    options={stateOptions}
                  />
                )}
              />
            </div>
            <div className="addressPage__inputContainer">
              <span>ZIP Code</span>
              <input
                {...register("zipCode", { required: true })}
                className="addressPage__zipInput"
                type="text"
                placeholder=""
              ></input>
            </div>
          </div>
          <button type="submit" value="Submit Form">
            Use this Address
          </button>
        </form>
        <div className="addressPage__buttonContainer"></div>
      </div>
    </div>
  );
};

export default AdressPage;
