import React, { useState } from "react";
import { useForm, Controller } from "react-hook-form";
import Select from "react-select";

import HeaderImage from "../../assets/address-page-header.png";
import AddressCard from "../../components/address-card/address-card.component";

import { connect } from "react-redux";
import { addAddress } from "../../redux/address/address.action";

import { countryOptions, stateOptions } from "./address-page.data";

import { withRouter } from "react-router-dom";

import "./address-page.styles.css";

const AdressPage = ({ address, addAddress, history }) => {
  const [addressData, setAddressData] = useState();
  const { register, control, handleSubmit } = useForm();

  const onFormSubmit = (data) => {
    // setAddressData(data);
    addAddressData(data);
    goPaymentPage(data);
  };

  const onErrors = (errors) => alert("Complete the form");

  const addAddressData = (addressData) => {
    addAddress(addressData);
  };

  const goPaymentPage = (addressData) => {
    if (addressData) {
      history.push("/payment");
    }
  };

  console.log("KAKA", addressData);
  console.log("ADREESREDux", address);
  console.log("history", history);
  return (
    <div className="addressPage__container">
      <div className="addressPage__subContainer">
        <div className="addressPage__header">
          <img alt="" src={HeaderImage}></img>
        </div>
        <h1 className="addressPage__title">Select a shipping address</h1>
        <hr />
        {address.address ? <AddressCard /> : null}
        <hr />
        <form onSubmit={handleSubmit(onFormSubmit, onErrors)}>
          <div className="addressPage__firstRow">
            <h3>Add new Address</h3>
            <span>Country/Region</span>
            <Controller
              name="country"
              control={control}
              defaultValue={false}
              rules={{ required: true }}
              render={({ field }) => (
                <Select
                  {...field}
                  className="addressPage__countryInput"
                  classNamePrefix="addressPage__countryInput"
                  options={countryOptions}
                />
              )}
            />
            <span>Full name (First and Last name</span>
            <input
              {...register("name", { required: true })}
              type="text"
              placeholder=""
            ></input>
            <span>Phone number</span>
            <input
              {...register("phone", { required: true })}
              type="text"
              placeholder=""
            ></input>
            <span>Address</span>
            <input
              {...register("nameStreet", { required: true })}
              type="text"
              placeholder="Street address or P.O. Box"
            ></input>
            <input
              name="numberStreet"
              {...register("numberStreet", { required: true })}
              type="text"
              placeholder="Apt, suite, unit, building, floor, etc."
            ></input>
          </div>
          <div className="addressPage__secondRow">
            <div className="addressPage__inputContainer">
              <span>City</span>
              <input
                {...register("city", { required: true })}
                className="addressPage__cityInput"
                type="text"
                placeholder=""
              ></input>
            </div>

            <div className="addressPage__inputContainer">
              <span>State</span>
              <Controller
                name="state"
                control={control}
                defaultValue={false}
                rules={{ required: true }}
                render={({ field }) => (
                  <Select
                    {...field}
                    className="addressPage__stateInput"
                    classNamePrefix="addressPage__stateInput"
                    options={stateOptions}
                  />
                )}
              />
            </div>
            <div className="addressPage__inputContainer">
              <span>ZIP Code</span>
              <input
                {...register("zipCode", { required: true })}
                className="addressPage__zipInput"
                type="text"
                placeholder=""
              ></input>
            </div>
          </div>
          <button
            className="addressPage__button"
            type="submit"
            value="Submit Form"
          >
            Use this Address
          </button>
        </form>
        <div className="addressPage__buttonContainer"></div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  address: state.address,
});

const mapDispatchToProps = (dispatch) => ({
  addAddress: (address) => dispatch(addAddress(address)),
});

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(AdressPage)
);
