import styled from "styled-components";

export const CardContainer = styled.div`
  width: 250px;
  display: flex;
  margin: 20px;
`;

export const ImageCarousel = styled.img`
  width: 200px;
  height: 200px;
  object-fit: cover;
`;

export const CarouselCardTitle = styled.h3`
  color: black;
  z-indez: 100;
`;
