import React from "react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

import CarouselCard from "../carousel-card/carousel-card.component";
import CustomDot from "../custom-dots/custom-dots.component";
import {
  CustomRightArrow,
  CustomLeftArrow,
} from "../custom-arrows/custom-arrows.component";

import { connect } from "react-redux";

import "./multi-carousel.styles.css";

const MultiCarousel = ({ carousel, title, collection, category }) => {
  // console.log("Colelele", collection);
  const responsive = {
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 5,
      slidesToSlide: 2, // optional, default to 1.
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
      slidesToSlide: 2, // optional, default to 1.
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
      slidesToSlide: 1, // optional, default to 1.
    },
  };

  return (
    <div className="carousel">
      <div className="carouselTitle__container">
        <h2 className="carousel__title1">{title}</h2>
        <span className="carousel__linkContainer">
          <a className="carousel__link">Shop Now</a>
        </span>
      </div>

      <Carousel
        containerClass="carousel__container"
        itemClass="carousel__item"
        sliderClass="carousel__slider"
        centerMode={true}
        showDots={true}
        dotListClass="carousel__dots"
        responsive={responsive}
        customRightArrow={<CustomRightArrow />}
        customLeftArrow={<CustomLeftArrow />}
        customDot={<CustomDot />}
      >
        <h1>HOLA</h1>
        {collection[category].items.map((item) => {
          return (
            <CarouselCard item={item} title={collection[category].title} />
          );
        })}
      </Carousel>
    </div>
  );
};

const mapStateToProps = (state) => ({
  collection: state.product.collections,
});

export default connect(mapStateToProps)(MultiCarousel);

