import React from "react";

import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectCurrentUser } from "../../redux/user/user.selectors";

import "./dropdown-card.styles.css";

const DropdownCard = ({ handleAuthentication, currentUser }) => {
  const capitalize = (str) => {
    return str.charAt(0).toUpperCase() + str.slice(1);
  };

  return (
    <div className="dropdown">
      <div className="dropbtn">
        {" "}
        <div onClick={handleAuthentication} className="header__option">
          <span className="header__optionLineOne">
            Hello {!currentUser ? "Guest" : capitalize(currentUser.displayName)}
          </span>
          <strong className="header__optionLineOne">Account & Lists</strong>
        </div>
      </div>
      <div class="dropdown-content">
        <a href="#">Link 1</a>
        <a href="#">Link 2</a>
        <a href="#">Link 3</a>
      </div>
    </div>
  );
};

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
});

export default connect(mapStateToProps)(DropdownCard);
