import React from "react";

import { connect } from "react-redux";
import { selectCollectionsForPreview } from "../../redux/products/product.selectors";
import ProductDetailsView from "../product-details-view/product-details-view.component";

import "./product-details.styles.css";

const ProductDetails = ({ collection, history }) => {
  const { title, items } = collection;
  console.log("PRODUCT DETAILS", collection);
  return (
    <div>
      PRODUCT DETAILS
      {collection.map((item) => (
        <ProductDetailsView key={item.id} item={item} history={history} />
      ))}
    </div>
  );
};

const mapStateToProps = (state, ownProps) => {
  console.log("KIWI", ownProps.match, ownProps);
  return {
    collection: selectCollectionsForPreview(state),
  };
};

export default connect(mapStateToProps)(ProductDetails);
