import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

import CarouselCard from "../carousel-card/carousel-card.component";

const responsive = {
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 4,
    slidesToSlide: 3, // optional, default to 1.
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2,
    slidesToSlide: 2, // optional, default to 1.
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1,
    slidesToSlide: 1, // optional, default to 1.
  },
};

const MultiCarousel = () => {
  return (
    <div className="carousel">
      <Carousel
        responsive={responsive}
        sliderClass="carousel1"
        itemClass="react-multi-carousel-item react-multi-carousel-item--active culo"
        containerClass="carousel3"
      >
        <div>
          <img src="https://m.media-amazon.com/images/I/61HxIbHP6AL._AC_SY400_.jpg" />
        </div>
        <CarouselCard
          img={"https://m.media-amazon.com/images/I/61HxIbHP6AL._AC_SY400_.jpg"}
        />

        <CarouselCard
          img={"https://m.media-amazon.com/images/I/61HxIbHP6AL._AC_SY400_.jpg"}
        />

        <CarouselCard
          img={"https://m.media-amazon.com/images/I/61HxIbHP6AL._AC_SY400_.jpg"}
        />

        <CarouselCard
          img={"https://m.media-amazon.com/images/I/61HxIbHP6AL._AC_SY400_.jpg"}
        />
      </Carousel>
    </div>
  );
};

export default MultiCarousel;
