import styled from "styled-components";

export const ComponentContainer = styled.div`
  display: flex;
  width: 100%;
  margin-top: 20px;
  margin-bottom: 20px;
  background-color: white;
  border: red solid 1px;
`;

export const CheckoutImage = styled.img`
  object-fit: contain;
  width: 220px;
  height: 220px;
`;

export const CheckoutInfo = styled.div`
  padding-left: 20px;
  width: 60%;
  border: blue solid 1px;
`;

export const CheckoutTitle = styled.p`
  font-size: 18px !important;
  line-height: 24px !important;
  font-weight: 800;
  color: #007185;
`;

export const CheckoutPrice = styled.p`
  border: solid 1px green;
  margin-left: 45px;
  font-size: 18px !important;
  line-height: 24px !important;
`;

export const CheckoutQuantity = styled.span`
  display: flex;
  align-items: center;
  margin-top: 10px;
  border: solid 1px red;
`;

export const CheckoutArrow = styled.div`
  cursor: pointer;
`;

export const CheckoutValue = styled.span`
  margin: 0 10px;
`;

export const CheckoutRating = styled.div`
  display: flex;
`;

export const CheckoutButton = styled.button`
  background: #f0c14b;
  border: 1px solid;
  margin-left: 20px;
  border-color: #a88734 #9c7e31 #846a29;
  color: #111;
  cursor: pointer;
`;
