import React from "react";

import CurrencyFormat from "react-currency-format";
import { createStructuredSelector } from "reselect";

import { connect } from "react-redux";
import {
  selectCartItemCount,
  selectCartTotal,
} from "../../redux/cart/cart.selectors";

import {
  SubtotalContainer,
  GiftTextContainer,
  GiftInput,
  SubtotalButton,
} from "./subtotal.styles";
import { useHistory } from "react-router-dom";

const Subtotal = ({ itemCount, total }) => {
  const history = useHistory
  return (
    <SubtotalContainer className="subtotal">
      <CurrencyFormat
        renderText={(value) => (
          <>
            <p>
              Subtotal ({itemCount} items):
              <strong>{value}</strong>
            </p>
            <GiftTextContainer className="subtotal__gift">
              <GiftInput type="checkbox" />
              This order contains a gift
            </GiftTextContainer>
          </>
        )}
        decimalScale={2}
        value={total} //part of the homework
        displayType={"text"}
        thousandSeparator={true}
        prefix={"$"}
      />

      <SubtotalButton onClick={ e=> history.pushState("/payment")}>Proced to Checkout</SubtotalButton>
    </SubtotalContainer>
  );
};

const mapStateToProps = (state) =>
  createStructuredSelector({
    itemCount: selectCartItemCount,
    total: selectCartTotal,
  });

export default connect(mapStateToProps)(Subtotal);
