import React, { useState, useEffect } from "react";

import { connect } from "react-redux";

import MenuItem from "../menu-item/menu-item.component";
import SigninCard from "../sign-in-card/signin-card.component";
import Ad from "../ad/ad.component";
import MultiCarousel from "../../components/multi-carousel/multi-carousel.component";
import COLLECTION__DATA from "./collections.data";

import {
  ComponentContainer,
  ComponentSubContainer,
  FirstRow,
  SpecialCard,
  SpecialCardTop,
  Button,
  SecondRow,
} from "./directory.styles";
import CAROUSEL__DATA from "../../redux/carousel/carousel.data";

const Directory = ({ collections }) => {
  const [carouse, setCarousel] = useState(null);
  const [height, sethight] = useState(null);

  const heightRef = React.createRef();

  useEffect(() => {
    sethight(heightRef.current.clientHeight);
    calcularAltura();
    window.addEventListener("resize", calcularAltura);
  }, []);

  const calcularAltura = () => {
    if (!null) {
      return sethight(heightRef.current?.clientHeight);
    }
  };

  // const { advert } = collections[2];
  console.log("TOTRA", collections);

  if (collections)
    return (
      <ComponentContainer height={height} className="directory-menu">
        <ComponentSubContainer className="estoy aqui" ref={heightRef}>
          <FirstRow className="first__row">
            <MenuItem props={collections[0]} />
            <MenuItem props={collections[1]} />
            <MenuItem props={collections[2]} />
            <SpecialCard className="special__card">
              <SpecialCardTop className="special__card__top">
                <SigninCard props={collections[3]} />
              </SpecialCardTop>
              <Button className="special__card__buttom">
                <Ad />
              </Button>
            </SpecialCard>
            <MenuItem props={collections[3]} />
            <MenuItem props={collections[4]} />
            <MenuItem props={collections[5]} />
            <MenuItem props={collections[6]} />
          </FirstRow>

          {/* <MultiCarousel category={"shopcategory"} /> */}
          {/* <MultiCarousel category={"computers"} /> */}

          <SecondRow className="second__row">
            <MenuItem props={collections[7]} />
            <MenuItem props={collections[8]} />
            <MenuItem props={collections[9]} />
            <MenuItem props={collections[10]} />
          </SecondRow>

          {/* <MultiCarousel category={"amazonbasics"} />
          <MultiCarousel category={"laptops"} /> */}
        </ComponentSubContainer>
      </ComponentContainer>
    );
  return <h1>FALTAN LOS DATOS</h1>;
};

const mapStateToProps = (state) => {
  console.log("ESTADO", state);
  return {
    collections: state.product.collections,
  };
};

export default connect(mapStateToProps)(Directory);
