import CartActionTypes from "./cart.types";
import { addItemToCart } from "./cart.utils";

const INITIAL_STATE = {
  cartItems: [],
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case CartActionTypes.ADD_ITEM:
      console.log("estado", state);
      return {
        ...state,
        cartItems: ["hola"],
      };

    default:
      return state;
  }
};

export default cartReducer;
