import styled from "styled-components";

export const ComponentContainer = styled.div`
  display: flex;
  align-items: center;
  justify-items: center;
  cursor: pointer;
  margin-top: 3px;
  margin-bottom: 3px;
  margin-left: 5px;
  margin-right: 5px;
`;

export const ComponentSubContainer = styled.div`
  margin-left: 5px;
  font-size: 14px;
`;
