import styled from "styled-components";

export const FooterContainer = styled.div`
  display: flex;
  justify-content: center;
  background-color: #232f3e;
  color: white;
  height: 300px;
  width: 1000px;
  margin-left: auto;
  margin-right: auto;
`;

export const FooterSubContainer = styled.div`
  width: 800px;
  display: flex;
  justify-content: space-evenly;
`;

export const FooterListContainer = styled.ul`
  display: flex;
  flex-direction: column;
  margin-left: 20px;
  width: 250px;
`;

export const FooterListItem = styled.li`
  padding: 5px;
`;

export const TitleList = styled.h3`
  font-size: 16px;
  margin: 6px 0 10px 0;
  font-weight: 700;
  color: #fff;
`;
