import React from "react";
import "./header.styles.css";

import SearchIcon from "@material-ui/icons/Search";

import { FiShoppingCart } from "react-icons/fi";

import SubHeader from "../subheader/subheader.component";

import { Link } from "react-router-dom";
import { connect } from "react-redux";

import { createStructuredSelector } from "reselect";
import {
  selectCartItemCount,
  selectCartTotal,
} from "../../redux/cart/cart.selectors";
import { selectCurrentUser } from "../../redux/user/user.selectors";

import { auth } from "../../firebase/firebase";

import Flag from "../../assets/lithuania.png";

const Header = ({ itemCount, currentUser }) => {
  const handleAuthentication = () => {
    auth.signOut();
  };

  const capitalize = (str) => {
    return str.charAt(0).toUpperCase() + str.slice(1);
  };

  return (
    <div className="header__container">
      <div className="header">
        <Link to="/">
          <img
            alt="header__logo"
            className="header__logo"
            src="https://pngimg.com/uploads/amazon/amazon_PNG11.png"
          />
        </Link>

        <div className="header__search">
          <input className="header__searchInput" type="text" />
          <SearchIcon className="header__searchIcon" />
        </div>

        <div className="header__option">
          <img alt="" src={Flag} className="" />
        </div>

        <div onClick={handleAuthentication} className="header__nav">
          <Link to={!currentUser && "/login"}>
            <div className="dropdown">
              <div className="headerOption__container">
                <div className="header__option">
                  <span className="header__optionLineOne">
                    Hello{" "}
                    {!currentUser
                      ? "Guest"
                      : capitalize(currentUser.displayName)}
                  </span>
                  <strong className="header__optionLineOne">
                    Account & Lists
                  </strong>
                </div>
              </div>

              <div className="container">
                <div className="dropdown-content">
                  <div className="dropdown__cardButton">Sign In</div>
                  <span className="dropdown__newCustomerTag">
                    New customer?
                    <span className="dropdown__startHereTag">Start here</span>
                  </span>
                  <div className="dropdown__cardButtonList">
                    <div>
                      <h4>Your List</h4>
                      <ul>
                        <li>Create a List</li>
                        <li>Find a List or Registry</li>
                        <li>AmazonSmile Charity Lists</li>
                      </ul>
                    </div>
                    <div>
                      <h4>Your Account</h4>
                      <ul>
                        <li>Account</li>
                        <li>Orders</li>
                        <li>Recommendations</li>
                        <li>Browsing History</li>
                        <li>Watchlist</li>
                        <li>Video Purchases & Rentals</li>
                        <li>Kindle Unlimeted</li>
                        <li>Content & Devices</li>
                        <li>Subscribe & Save Items</li>
                        <li>Memberships & Subscriptions</li>
                        <li>Music Library</li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </Link>
          <div className="headerOption__container">
            <div className="header__option">
              <span className="header__optionLineOne">Returns</span>
              <strong className="header__optionLineOne">& Orders</strong>
            </div>
          </div>

          {/* <div className="headerOption__container">
            <div onClick={() => auth.signOut()} className="header__option">
              <span className="header__optionLineOne">Your</span>
              <span className="header__optionLineOne">Prime</span>
            </div>
          </div> */}
        </div>

        <Link to="/checkout">
          <div className="headerOption__container">
            <div className="header__optionBasket">
              <FiShoppingCart size={45} />
              <span
                className={`${
                  itemCount > 9 ? "itemCount__addSpace" : ""
                } item__count`}
              >
                {itemCount}
              </span>
            </div>
          </div>
        </Link>
      </div>
      <SubHeader />
    </div>
  );
};

const mapStateToProps = createStructuredSelector({
  itemCount: selectCartItemCount,
  total: selectCartTotal,
  currentUser: selectCurrentUser,
});

export default connect(mapStateToProps)(Header);
