      <div className="product__info">
        <p>{title}</p>
        <p className="product__price">
          <small>$</small>
          <strong>{price}</strong>
        </p>
        <div className="product__rating">
          {Array(rating)
            .fill()
            .map((_, i) => (
              <p>‚≠ê</p>
            ))}
        </div>
      </div>

      <img src={image} alt="" />

      <button onClick={() => addItem(id, title, image, price, rating)}>
        Add to Basket
      </button>
    </div>
  );
};

export default CollectionItem;
