import React from "react";
import { useEffect } from "react";
import { connect } from "react-redux";

import { selectOneProduct } from "../../redux/products/product.selectors";

import "./product-details.styles.css";

const ProductDetails = ({ collection, history }) => {
  console.log("mira lala", collection);

  useEffect(() => {
    const urlPath = history.location.pathname;
    const a = urlPath.split("/")[1];
    const b = collections[a].items;
    const c = urlPath.split("/")[2];
    console.log("que es esto", b[0].linkUrl);
    for (let i = 0; i < b.length; i++) {
      if (b[i].tag === c) {
        console.log("Estoy dentro");
        return b[i];
      } else {
        return console.log("no ha funcionado");
      }
    }
  }, []);

  return <div>{collection.name}</div>;
};

const mapStateToProps = (state, ownProps) => {
  console.log("KIWI", ownProps.match.url);
  return {
    collection: selectOneProduct(ownProps.match.url)(state),
  };
};

export default connect(mapStateToProps)(ProductDetails);
