import React, { useState } from "react";
import { useEffect } from "react";
import { connect } from "react-redux";

import "./product-details.styles.css";

const ProductDetails = ({ collection, history }) => {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    console.log("QUE ES COLLECTION", collection);
    setIndex(getIndex());
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const getIndex = () => {
    const urlPath = history.location.pathname;
    const productId = urlPath.split("/")[2];
    console.log("aaaaa", collection[0].id);

    for (let i = 0; i < collection.length; i++) {
      if ((collection[i].linkUrl) === productId) {
        return i;
      }
    }
  };

  const {
    imageUrl,
    descriptionTitle,
    fullDescription,
    name,
    price,
    rating,
    id,
  } = collection[index];

  return (
    <div className="reviewProduct__container">
      <div className="reviewProduct__subContainer">
        <div className="reviewProduct__leftSide">
          LEFT SIDE
          <div className="reviewProduct__imageContainer">
            {<img alt="" src={imageUrl} />}
          </div>
        </div>

        <div className="reviewProduct__rightSide">
          RIGHT SIDE
          <div className="reviewProduct__title">{descriptionTitle}</div>
          <div className="reviewProduct__rating">
            {" "}
            {Array(rating)
              .fill()
              .map((_, i) => (
                <p>⭐</p>
              ))}
          </div>
          <hr className="reviewProduct__line" />
          <div className="reviewProduct__fullDescription">
            {fullDescription}
          </div>
        </div>

        <div className="reviewProduct__paymentCardContainer">
          <div className="reviewProduct__paymentSubCardContainer">{price}€</div>
        </div>
      </div>
    </div>
  );
};

