import React from "react";

import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectCurrentUser } from "../../redux/user/user.selectors";

import { auth } from "../../firebase/firebase";

import "./dropdown-signin-card.styles.css";

const DropdownCard = ({ show, currentUser, history }) => {
  // const capitalize = (str) => {
  //   return str.charAt(0).toUpperCase() + str.slice(1);
  // };

  const handleAuthentication = () => {
    auth.signOut();
  };

  return (
    <div className="dropdown">
      <div className={`${show ? "dropdown-content" : ""} `}>
        <div className="dropdown__triangleUp"></div>
        {currentUser ? (
          <div onClick={handleAuthentication} className="dropdown__cardButton">
            Sign Out
          </div>
        ) : (
          <div
            onClick={() => history.push("/login")}
            className="dropdown__cardButton"
          >
            Sign In
          </div>
        )}
        {currentUser ? (
          <span
            onClick={() => history.push("/signup")}
            className="dropdown__newCustomerTag"
          >
            New customer?
            <span className="dropdown__startHereTag">Start here</span>
          </span>
        ) : (
          ""
        )}
        <div className="dropdown__cardButtonList">
          <div>
            <h4>Your List</h4>
            <ul>
              <li>Create a List</li>
              <li>Find a List or Registry</li>
              <li>AmazonSmile Charity Lists</li>
            </ul>
          </div>
          <div>
            <h4>Your Account</h4>
            <ul>
              <li>Account</li>
              <li>Orders</li>
              <li>Recommendations</li>
              <li>Browsing History</li>
              <li>Watchlist</li>
              <li>Video Purchases & Rentals</li>
              <li>Kindle Unlimeted</li>
              <li>Content & Devices</li>
              <li>Subscribe & Save Items</li>
              <li>Memberships & Subscriptions</li>
              <li>Music Library</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
});

export default withRouter(connect(mapStateToProps)(DropdownCard));
