import React from "react";

import { connect } from "react-redux";

import {
  addItem,
  clearItemFromCart,
  removeItem,
} from "../../redux/cart/cart.action";

import {
  ComponentContainer,
  LeftSide,
  RightSide,
  Image,
  Info,
  Title,
  Price,
  Small,
  Strong,
  Quantity,
  Arrow,
  Value,
  Button,
} from "./checkout.styles";

const CheckoutItem = ({
  cartItem,
  hideButton,
  clearItem,
  addItem,
  removeItem,
  history,
}) => {
  const { imageUrl, price, quantity, descriptionTitle, linkUrl } = cartItem;

  return (
    <ComponentContainer className="checkoutProduct">
      <LeftSide>
        <Image
          onClick={() => history.push(`shopcategory${linkUrl}`)}
          className="checkoutProduct__image"
          src={imageUrl}
          alt=""
        />
        <Info className="checkoutProduct__info">
          <Title className="checkoutProduct__title">{descriptionTitle}</Title>
          <Quantity className="quantity">
            <Arrow className="arrow" onClick={() => removeItem(cartItem)}>
              &#10094;
            </Arrow>
            <Value className="value">{quantity}</Value>
            <Arrow className="arrow" onClick={() => addItem(cartItem)}>
              &#10095;
            </Arrow>
            {!hideButton && (
              <Button onClick={() => clearItem(cartItem)}>
                Remove from Basket
              </Button>
            )}
          </Quantity>
        </Info>
      </LeftSide>

      <Price className="checkoutProduct__price">
        <>
          <Small>$</Small>
          <Strong>{price}</Strong>
        </>
      </Price>
    </ComponentContainer>
  );
};

const mapToDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
  clearItem: (item) => dispatch(clearItemFromCart(item)),
  removeItem: (item) => dispatch(removeItem(item)),
});

export default connect(null, mapToDispatchToProps)(CheckoutItem);
