import styled from "styled-components";

export const FooterContainer = styled.div`
  display: flex;
  justify-content: center;
  background-color: #232f3e;
  color: white;
  height: 350px;
  padding-top: 50px;
`;

export const FooterSubContainer = styled.div`
  display: flex;
  justify-content: space-evenly;
  width: 1000px;
  margin-left: auto;
  margin-right: auto;
`;

export const FooterListContainer = styled.ul`
  display: flex;
  flex-direction: column;
  margin-left: 20px;
  width: 250px;
`;

export const FooterListItem = styled.li`
  padding-left: 0px;
  padding-right: 0px;
  padding-bottom: 10px;
  font-size: 14px;
`;

export const TitleList = styled.h3`
  font-size: 16px;
  font-weight: 700;
  margin: 6px 0 10px 0;
  font-weight: 700;
  color: #fff;
`;
